import { saveQuestion , saveQuestionAnswer } from "../utils/api";

export const RECEIVE_QUESTIONS = "RECEIVE_QUESTIONS"
export const ADD_QUESTION      = "ADD_QUESTION"
export const ADD_ANSWER        =  "ADD_ANSWER"


export function receiveQuestions(questions) {
    return {
      type: RECEIVE_QUESTIONS,
      questions,
    };
  }

  export function addQuestion({ id, authedUser, answer }) {
    return {
      type: ADD_QUESTION,
      id,
      authedUser,
      answer
    }

  }

  export function handleAddQuestion(info) {
    return (dispatch) => {
      dispatch(addQuestion(info));

      return saveQuestion(info).catch((e) => {
        console.warn("Error in handleAddQuestion" ,e);
        dispatch(addQuestion(info));
        alert('There was an error saving the guestion. Try againg')
      })

    }

  }
//save the answer 
  export function addQuestionAnswer(question) {
    return {
      type: ADD_ANSWER,
      question,
    }

  }

  export function handleAddQuestionAnswer({ qid, answer }) {
    return (dispatch, getState) => {
      const { authedUser } = getState();

      // dispatch(showLoading());

      return saveQuestionAnswer({
        authedUser,
        qid,
        answer
      })
      .then((question) => dispatch(addQuestionAnswer(question)))
      //.then(() => dispatch(hideLoading()));
    }

  }
