import { Fragment, useEffect } from "react";
import { handleInitialData } from "../actions/shared";
import { connect } from "react-redux";
import Dashboard from "./Dashboard";
import Nav from "./Nav";
import { Routes, Route } from "react-router";
import NewQuestion from "./NewQuestion";
import LeaderBoard from "./LeaderBoard";
import NotFound from "./404page"
import Login from './Login'
import PollAnswer from "./PollAnswer";
import PrivateRoute from "./PrivateRoute"



const App = (props) => {
  console.log("App props", props)

   useEffect(() => {
      props.dispatch(handleInitialData());
    }, []);


  return (
    <div>
      <Fragment>
        <div className="container"></div>
        {props.authedUser && <Nav/>}

          <Routes>
             <Route path="/"       exact element={<PrivateRoute><Dashboard /></PrivateRoute>}/>
             <Route path="/question/:id" element={<PrivateRoute><PollAnswer/></PrivateRoute>}/>
             <Route path="/leaderdoard"  element={<PrivateRoute><LeaderBoard/></PrivateRoute>}/>
             <Route path="/add"          element={<PrivateRoute><NewQuestion/></PrivateRoute>}/>
             <Route path="/login"        element={<Login/>}/>
             <Route path="*"             element={<NotFound/>} />
          </Routes>

      </Fragment>
    </div>
  );
}

const mapStateToProps = ({ authedUser, }) => ({
  loading: authedUser === null,
  authedUser : !!authedUser
})

export default  connect(mapStateToProps)(App);
