import React, { useState } from 'react';

const CustomDropdown = ({ options, label, onSelect }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
    onSelect(option);
    setIsOpen(false);
  };

  return (
    <div className="dropdown">
      <button className="dropdown-button" onClick={toggleDropdown}>
        {selectedOption || label}
      </button>
      {isOpen && (
        <ul className="dropdown-menu">
          {options.map((option) => (
            <li key={option.value} onClick={() => handleOptionSelect(option)}>
              {option.label}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

const LogingPAge = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleUsernameSelect = (option) => {
    setUsername(option.value);
  };

  const handlePasswordSelect = (option) => {
    setPassword(option.value);
  };

  return (
    <div>
      <CustomDropdown
        label="Select Username"
        options={[
          { value: 'user1', label: 'User 1' },
          { value: 'user2', label: 'User 2' },
        ]}
        onSelect={handleUsernameSelect}
      />
      <CustomDropdown
        label="Select Password"
        options={[
          { value: 'pass1', label: 'Password 1' },
          { value: 'pass2', label: 'Password 2' },
        ]}
        onSelect={handlePasswordSelect}
      />
      <button type="submit">Login</button>
    </div>
  );
};

export default connect()(LogingPage);
