import { connect } from "react-redux"
import { useLocation, useNavigate, useParams } from "react-router-dom";

const withRouter = (Component) => {
    const ComponentWithRouterProp = (props) => {
      let location = useLocation();
      let navigate = useNavigate();
      let params = useParams();
      return <Component {...props} router={{ location, navigate, params }} />;
    };

    return ComponentWithRouterProp;
  };


const LeaderBoard = ({users, questions, authedUser }) => {
    const data = [
        { id: 1, name: 'John Doe', age: 25, email: 'john.doe@example.com' },
        { id: 2, name: 'Jane Smith', age: 30, email: 'jane.smith@example.com' },
        { id: 3, name: 'Peter Jones', age: 35, email: 'peter.jones@example.com' },
      ];
      const question = questions;
      const name = users[questions.author].name
      const avatar   = users[question.author].avatarURL

      return (
        <div>
            <table>
            <thead>
                <tr>
                <th>Name</th>
                <th>Answer</th>
                <th>Poll</th>
                </tr>
            </thead>
            <tbody>
                {data.map(row => (
                <tr key={row.id}>
                    <td><img src={avatar} alt={`Avar of ${name}`} className="avatar" />{row.id}</td>
                    <td>{row.name}</td>
                    <td>{row.age}</td>
                    <td>{row.email}</td>
                </tr>
                ))}
            </tbody>
            </table>
        </div>
      );
}
const mapStateToProps = ({ authedUser, users, questions,}, props) => {
    // const { id }= props.router.params;
    return {
      //id,
      users,
      questions,
      authedUser
    }
  }
export default withRouter(connect(mapStateToProps)(LeaderBoard))
