import { connect } from "react-redux"
import { formatDate, formatQuestion } from "../utils/helper"
import { useNavigate } from "react-router"


const Questions = (props) => {
    const navigate = useNavigate();

 const voteOptioneOne = questions[id].optionOne.votes.includes(authedUser)
  const voteOptioneTwo = questions[id].optionTwo.votes.includes(authedUser)
  const hasVoted = voteOptioneOne || voteOptioneTwo;

    if(props.questions === null) {
        return <div>This question doesn't exist.</div>
    }

    const {id, name, avatar, timestamp } = props.question

    const toParent = (e, id) => {
        e.preventDefault();
        navigate(`/question/${id}`);  
    }
    return (
        <div>

          {hasVoted && (  <div className="question">
            <img src={avatar} alt={`Avar of ${name}`} className="avatar" />
            <div className="question-info">
                <div>
                    <span>{name}</span>
                    <div>{formatDate(timestamp)}</div>
                </div>
                <button className="show-bnt" onClick={ (e) => toParent(e,id) }>show</button>

            </div>
        </div>)}

        </div>



    )
}

const mapStateToProps = ({authedUser, users, questions }, {id}) => {
    const question = questions[id];

    return{
        authedUser,
        question: question 
        ? formatQuestion(question, users[question.author], authedUser) 
        : null
    }

}

export default connect(mapStateToProps)(Questions);
