import { connect } from "react-redux"

const LeaderBoard = (props) => {
    const data = [
        { id: 1, name: 'John Doe', age: 25, email: 'john.doe@example.com' },
        { id: 2, name: 'Jane Smith', age: 30, email: 'jane.smith@example.com' },
        { id: 3, name: 'Peter Jones', age: 35, email: 'peter.jones@example.com' },
      ];
      const { dispatch, id, users,authedUser, questions, questionsIds } = props;
        const question = questions[id];
        const avatar   = users[question.author].avatarURL
        const name     = users[question.author].name

      return (
        <div>
            <table>
            <thead>
                <tr>
                <th>Name</th>
                <th>Answer</th>
                <th>Poll</th>
                </tr>
            </thead>
            <tbody>
                {data.map(row => (
                <tr key={row.id}>
                    <td><img src={avatar} alt={`Avar of ${name}`} className="avatar" />{row.id}</td>
                    <td>{row.name}</td>
                    <td>{row.age}</td>
                    <td>{row.email}</td>
                </tr>
                ))}
            </tbody>
            </table>
        </div>
      );
}
const mapStateToProps = ({ authedUser, users, questions,}, props) => {
    //const { id }= props.router.params;



    return {
      id,
      users,
      questions,
      authedUser,
      questionsIds : Object.keys(questions).sort(
        (a, b) => questions[b].timestamp - questions[a].timestamp)
    }
  }

export default connect(mapStateToProps)(LeaderBoard)
