import { connect } from "react-redux"
import { formatQuestion } from "../utils/helper";



const LeaderBoard = (props) => {
    console.log("LeaderBoard props ", props)
    const data = [
        { id: 1, name: 'John Doe', age: 25, email: 'john.doe@example.com' },
        { id: 2, name: 'Jane Smith', age: 30, email: 'jane.smith@example.com' },
        { id: 3, name: 'Peter Jones', age: 35, email: 'peter.jones@example.com' },
      ];

    //   const fullName = questions[questionsIds];
    //   console.log("fullName ", fullName)
    const {id, name, avatar, timestamp } = props.question

      return (
        <div>
            <table>
            <thead>
                <tr>
                <th>Name</th>
                <th>Answer</th>
                <th>Poll</th>
                </tr>
            </thead>
            <tbody>
                {data.map(row => (
                <tr key={row.id}>
                    <td>{row.id}</td>
                    <td>{row.name}</td>
                    <td>{row.age}</td>
                    <td>{row.email}</td>
                </tr>
                ))}
            </tbody>
            </table>
        </div>
      );
}
const mapStateToProps = ({authedUser, users, questions }, {id}) => {
    const question = questions[id];

    return{
        authedUser,
        question: question 
        ? formatQuestion(question, users[question.author], authedUser) 
        : null
    }

}

export default connect(mapStateToProps)(LeaderBoard)
