import { RECEIVE_QUESTIONS, ADD_QUESTION, ADD_ANSWER } from "../actions/questions";

export default function questions(state = {}, action) {
    switch(action.type) {
        case RECEIVE_QUESTIONS:
            return {
                ...state,
                ...action.questions,
            }
        case ADD_QUESTION:
            return {
                ...state,
                ...[action.id],
                [action.answer] : {
                    ...state[action.id][action.answer],
                    votes: state[action.id][action.answer].text.conact([action.authedUser])

                }
            }
        case ADD_ANSWER:
            //const { question  } = action
            return {
                ...state,
                ...[action.id],
                [action.answer] : 
                [action.answer] == "optionOne" ?
                    { ...state[action.id][action.answer],
                    votes: state[action.id][action.answer].text.conact([action.authedUser])} :
                    { ...state[action.id][action.answer],
                        votes: state[action.id][action.answer].text.conact([action.authedUser])} 





            }
        default:
            return state;
    }

}
