import { connect } from "react-redux"




const LeaderBoard = (props) => {
    console.log("LeaderBoard props ", props)

    //   const fullName = questions[questionsIds];
    //   console.log("fullName ", fullName)
  //  const {id, name, avatar, timestamp } = props.questions

      return (
        <div>
            <table>
            <thead>
                <tr>
                <th>Name</th>
                <th>Answer</th>
                <th>Poll</th>
                </tr>
            </thead>
            <tbody>
                {props.questionsIds.map(row => (
                <tr key={row}>
                    <td>
                        <p>
                            {props.users[props.questions[row].author].name }
                            ({props.questions[row].author})

                        </p> 
                    </td>
                </tr>
                ))}
            </tbody>
            </table>
        </div>
      );
}
const mapStateToProps = ({authedUser, users, questions }) => {


    return {
        authedUser,
        users,
        questions,
        questionsIds: Object.keys(questions).sort(
            (a, b) => questions[b].timestamp - questions[a].timestamp
          ),

    }

}

export default connect(mapStateToProps)(LeaderBoard)
