import { useState } from "react";
import { connect } from "react-redux";
import { setAuthedUser } from "../actions/authedUser";



const LogingPage = ({  dispatch , users }) => {
    console.log("Longing page ", users)
    const [success, setSuccess] = useState(false);
    const [error, setError] = useState(false);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleUsername= (e) => {
        e.preventDefault()
        const username = e.target.value;
       setUsername(username)
    }

    const handlePassword = (e) => {
        e.preventDefault()
        const password = e.target.value;
        setPassword(password)
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        const userExit = users.find((user) => 
            (user.id.includes(username) && user.password.includes(password)))   
        if(userExit) {
            dispatch(setAuthedUser(username)); 
            setSuccess(true)
            navigate("/");
        }     

    }
    // if (Object.values(users).includes('sarahedo')) {
    //     console.log('true');
    // } else {
    //     console.log('false');
    // }

    // users.map((user) => {
    //     if(user.id.includes('sarahedo') && user.password.includes('password123') ) {
    //         console.log('true');   
    //     } else {
    //         console.log('false');
    //     }

    // })
    // const valid = () => {
    //     let existsUser = customer.find((x) => (x.name === name && x.password === password))
    //     if(existsUser){
    //       alert('Navigate to new page')
    //     }
    //   }
    // const valid = users.find((user) => (user.id.includes('sarahedo') && user.password.includes('password123') ))
    // if(valid) {
    //     console.log("valid yes")
    // } else {
    //     console.log("valid no")

    // }


    return(
        <div className="container">
            <h1 className="center">Long In</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>username:</label>
                    <input
                        data-testid="email-input"
                        type="text"
                        placeholder="username..."
                        value={username}
                        onChange={handleUsername}
                    />
                </div>
                <div>
                    <label>password:</label>
                    <input
                        data-testid="email-input"
                        type="text"
                        placeholder="password..."
                        value={password}
                        onChange={handlePassword}
                    />
                </div>
                <input
                    data-testid="submit-button"
                    type="submit"
                    value="Submit"
                />
            </form>

        </div>
    )
}
const mapStateToProps = ({ users }) => {
    return { 
        //users,

       users : Object.values(users),
     }
}
export default connect(mapStateToProps)(LogingPage);
