import { useState } from "react";
import { connect } from "react-redux";


const LogingPage = ({ dispath , users }) => {
    console.log("Longing page ", users)
    const [success, setSuccess] = useState(false);
    const [error, setError] = useState(false);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleUsername= (e) => {
        e.preventDefault()
        const username = e.target.value;
       setUsername(username)
    }

    const handlePassword = (e) => {
        e.preventDefault()
        const password = e.target.value;
        setPassword(password)
    }

    const handleSubmit = (e) => {
        e.preventDefault();




    }
    // if (Object.values(users).includes('sarahedo')) {
    //     console.log('true');
    // } else {
    //     console.log('false');
    // }

    users.map((user) => {
        if(user.id.includes('sarahedo')) {
            console.log('true');   
        } else {
            console.log('false');
        }

    })


    return(
        <div className="container">
            <h1 className="center">Long In</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>username:</label>
                    <input
                        data-testid="email-input"
                        type="text"
                        placeholder="username..."
                        value={username}
                        onChange={handleUsername}
                    />
                </div>
                <div>
                    <label>password:</label>
                    <input
                        data-testid="email-input"
                        type="text"
                        placeholder="password..."
                        value={password}
                        onChange={handlePassword}
                    />
                </div>
                <input
                    data-testid="submit-button"
                    type="submit"
                    value="Submit"
                />
            </form>

        </div>
    )
}
const mapStateToProps = ({ users }) => {
    return { 
        //users,

       users : Object.values(users),
     }
}
export default connect(mapStateToProps)(LogingPage);
