import { connect } from "react-redux";
import Question from "./Question";



const withRouter = (Component) => {
  const ComponentWithRouterProp = (props) => {
    let location = useLocation();
    let navigate = useNavigate();
    let params = useParams();
    return <Component {...props} router={{ location, navigate, params }} />;
  };

  return ComponentWithRouterProp;
};

const Dashboard = ({questions, questionsIds, authedUser}) => {
  console.log("========> " , questions)
  // const voteOptioneOne = questions[questionsIds].optionOne.votes.includes(authedUser)
  // const voteOptioneTwo = questions[questionsIds].optionTwo.votes.includes(authedUser)
  // const hasVoted = voteOptioneOne || voteOptioneTwo;
    //console.log("Dashboard props ", props)
    return (
      <div className="card-grid">
        <h3 className="center">New Questions</h3>
        <ul className="dashboard-list">
            {questionsIds.map((id) =>(
                <li key={id}>
                  <Question id={id}/>
                </li>
            ))}
        </ul>
      </div>
    )
}

const mapStateToProps = ({ questions, authedUser}, props) => {
  const { id }= props.router.params;
return {
    questionsIds: Object.keys(questions).sort(
      (a, b) => questions[b].timestamp - questions[a].timestamp
    ),
    questions,
    authedUser,
    id
  }
};
export default withRouter(connect(mapStateToProps)(Dashboard));
